import React, { useState } from 'react';
import { Building2, Users, Target, TrendingUp, Lightbulb } from 'lucide-react';
import { apiService } from '../services/api';
import toast from 'react-hot-toast';

const Companies = () => {
  const [loading, setLoading] = useState(false);
  const [analysis, setAnalysis] = useState(null);
  const [domain, setDomain] = useState('');

  const sampleCompanyData = {
    analysis_data: {
      company_name: "Agentic.it",
      industry: "Artificial Intelligence, Enterprise Automation, SaaS",
      description: "Agentic.it appears to be a company focused on delivering agentic AI solutions—AI systems and platforms that enable autonomous, adaptive, and context-aware automation of complex business processes.",
      products_services: [
        "Agentic AI platforms for enterprise automation",
        "Cloud-based agent hosting (multi-tenant architecture)",
        "Integrated workflow agents for R&D, sales, marketing, logistics, finance, HR, and management"
      ],
      target_markets: [
        "Large enterprises seeking advanced automation",
        "Organizations with complex, cross-functional workflows"
      ],
      key_challenges: [
        "Scaling agentic AI solutions for mass adoption across diverse enterprise environments",
        "Ensuring secure, controlled cross-company data and workflow integration"
      ]
    }
  };

  const handleAnalyze = async () => {
    if (!domain.trim()) {
      toast.error('Please enter a domain');
      return;
    }

    setLoading(true);
    try {
      const response = await apiService.analyzeCompanyPersonasNew(domain, sampleCompanyData);
      setAnalysis(response.data);
      toast.success('Analysis completed successfully');
    } catch (error) {
      console.error('Analysis error:', error);
      toast.error('Failed to analyze company personas');
    } finally {
      setLoading(false);
    }
  };

  const handleUseSample = () => {
    setDomain('bicycle.ai');
    setLoading(true);
    setTimeout(() => {
      setAnalysis({
        analysis_data: {
          id: 9,
          domain: "bicycle.ai",
          analysis_data: {
            personas: [
              {
                type: "decision_maker",
                title: "Head of Procurement",
                department: "Procurement/Supply Chain",
                seniority_level: "Director/VP",
                typical_responsibilities: [
                  "Oversee supplier selection and contract negotiation",
                  "Approve major purchasing decisions for inventory and technology",
                  "Align procurement strategy with company objectives"
                ],
                pain_points: [
                  "Inefficient supplier discovery and onboarding",
                  "High procurement costs and long lead times",
                  "Lack of integration between procurement systems"
                ],
                influence_level: "high",
                budget_authority: true,
                linkedin_search_terms: ["Head of Procurement", "Director of Supply Chain", "VP Procurement"]
              },
              {
                type: "sponsor",
                title: "Chief Operations Officer (COO)",
                department: "Operations",
                seniority_level: "Executive",
                typical_responsibilities: [
                  "Drive operational efficiency across the organization",
                  "Champion digital transformation initiatives",
                  "Sponsor cross-departmental technology adoption"
                ],
                pain_points: [
                  "Operational silos between sales, procurement, and inventory",
                  "Resistance to change from legacy processes",
                  "Inability to scale operations efficiently"
                ],
                influence_level: "high",
                budget_authority: false,
                linkedin_search_terms: ["Chief Operations Officer", "COO Bicycle Industry", "VP Operations Cycling"]
              }
            ],
            targeting_strategy: "Prioritize outreach to decision makers in procurement and supply chain, leveraging value props around cost reduction, supplier discovery, and integration.",
            recommended_approach: "Use LinkedIn Sales Navigator to identify and segment target personas by title and department. Tailor messaging to each persona's pain points and KPIs."
          },
          created_at: "2025-08-05 23:33:52"
        },
        fromCache: true
      });
      setLoading(false);
      toast.success('Sample analysis loaded');
    }, 1000);
  };

  const getPersonaTypeColor = (type) => {
    const colors = {
      decision_maker: 'bg-red-100 text-red-800',
      sponsor: 'bg-blue-100 text-blue-800',
      influencer: 'bg-yellow-100 text-yellow-800',
      champion: 'bg-green-100 text-green-800'
    };
    return colors[type] || 'bg-gray-100 text-gray-800';
  };

  const getInfluenceLevelColor = (level) => {
    const colors = {
      high: 'text-red-600',
      medium: 'text-yellow-600',
      low: 'text-green-600'
    };
    return colors[level] || 'text-gray-600';
  };

  return (
    <div className="max-w-7xl mx-auto space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex items-center mb-4">
          <Building2 className="h-8 w-8 text-blue-600 mr-3" />
          <h1 className="text-2xl font-bold text-gray-900">Company Persona Analysis</h1>
        </div>
        <p className="text-gray-600 mb-6">
          Analyze company domains to identify key decision-makers and build targeted outreach strategies.
        </p>

        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <label htmlFor="domain" className="block text-sm font-medium text-gray-700 mb-2">
              Company Domain
            </label>
            <input
              type="text"
              id="domain"
              value={domain}
              onChange={(e) => setDomain(e.target.value)}
              placeholder="e.g., bicycle.ai"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="flex gap-2 items-end">
            <button
              onClick={handleAnalyze}
              disabled={loading}
              className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
            >
              {loading ? 'Analyzing...' : 'Analyze'}
            </button>
            <button
              onClick={handleUseSample}
              disabled={loading}
              className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
            >
              Use Sample
            </button>
          </div>
        </div>
      </div>

      {/* Results */}
      {analysis && analysis.analysis_data && (
        <div className="space-y-6">
          {/* Summary */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Analysis Summary</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="bg-gray-50 rounded-lg p-4">
                <div className="flex items-center">
                  <Building2 className="h-5 w-5 text-gray-600 mr-2" />
                  <span className="text-sm font-medium text-gray-600">Domain</span>
                </div>
                <p className="text-lg font-semibold text-gray-900 mt-1">{analysis.analysis_data.domain}</p>
              </div>
              <div className="bg-gray-50 rounded-lg p-4">
                <div className="flex items-center">
                  <Users className="h-5 w-5 text-gray-600 mr-2" />
                  <span className="text-sm font-medium text-gray-600">Personas</span>
                </div>
                <p className="text-lg font-semibold text-gray-900 mt-1">
                  {analysis.analysis_data.analysis_data?.personas?.length || 0}
                </p>
              </div>
              <div className="bg-gray-50 rounded-lg p-4">
                <div className="flex items-center">
                  <TrendingUp className="h-5 w-5 text-gray-600 mr-2" />
                  <span className="text-sm font-medium text-gray-600">Created</span>
                </div>
                <p className="text-lg font-semibold text-gray-900 mt-1">
                  {new Date(analysis.analysis_data.created_at).toLocaleDateString()}
                </p>
              </div>
            </div>
          </div>

          {/* Personas */}
          {analysis.analysis_data.analysis_data?.personas && (
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-6">Target Personas</h2>
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {analysis.analysis_data.analysis_data.personas.map((persona, index) => (
                  <div key={index} className="border border-gray-200 rounded-lg p-6">
                    <div className="flex items-start justify-between mb-4">
                      <div>
                        <div className="flex items-center mb-2">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getPersonaTypeColor(persona.type)}`}>
                            {persona.type.replace('_', ' ').toUpperCase()}
                          </span>
                          <span className={`ml-2 text-sm font-medium ${getInfluenceLevelColor(persona.influence_level)}`}>
                            {persona.influence_level} influence
                          </span>
                        </div>
                        <h3 className="text-lg font-semibold text-gray-900">{persona.title}</h3>
                        <p className="text-sm text-gray-600">{persona.department} • {persona.seniority_level}</p>
                        {persona.budget_authority && (
                          <span className="inline-flex items-center px-2 py-1 text-xs font-medium bg-green-100 text-green-800 rounded-full mt-2">
                            Budget Authority
                          </span>
                        )}
                      </div>
                    </div>

                    <div className="space-y-4">
                      <div>
                        <h4 className="text-sm font-medium text-gray-900 mb-2">Responsibilities</h4>
                        <ul className="text-sm text-gray-600 space-y-1">
                          {persona.typical_responsibilities?.slice(0, 3).map((resp, idx) => (
                            <li key={idx} className="flex items-start">
                              <span className="block w-1 h-1 bg-gray-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                              {resp}
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div>
                        <h4 className="text-sm font-medium text-gray-900 mb-2">Pain Points</h4>
                        <ul className="text-sm text-gray-600 space-y-1">
                          {persona.pain_points?.slice(0, 3).map((pain, idx) => (
                            <li key={idx} className="flex items-start">
                              <span className="block w-1 h-1 bg-red-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                              {pain}
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div>
                        <h4 className="text-sm font-medium text-gray-900 mb-2">LinkedIn Search Terms</h4>
                        <div className="flex flex-wrap gap-2">
                          {persona.linkedin_search_terms?.slice(0, 3).map((term, idx) => (
                            <span key={idx} className="inline-flex px-2 py-1 text-xs bg-blue-100 text-blue-800 rounded-md">
                              {term}
                            </span>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Strategy */}
          {analysis.analysis_data.analysis_data?.targeting_strategy && (
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Targeting Strategy</h2>
              <div className="bg-blue-50 rounded-lg p-4 mb-4">
                <div className="flex items-start">
                  <Target className="h-5 w-5 text-blue-600 mt-0.5 mr-3 flex-shrink-0" />
                  <p className="text-gray-800">{analysis.analysis_data.analysis_data.targeting_strategy}</p>
                </div>
              </div>

              {analysis.analysis_data.analysis_data.recommended_approach && (
                <div className="bg-green-50 rounded-lg p-4">
                  <div className="flex items-start">
                    <Lightbulb className="h-5 w-5 text-green-600 mt-0.5 mr-3 flex-shrink-0" />
                    <div>
                      <h3 className="font-medium text-gray-900 mb-2">Recommended Approach</h3>
                      <p className="text-gray-800">{analysis.analysis_data.analysis_data.recommended_approach}</p>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* Empty State */}
      {!analysis && !loading && (
        <div className="bg-white rounded-lg shadow-sm p-12 text-center">
          <Building2 className="h-16 w-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Analysis Yet</h3>
          <p className="text-gray-600 mb-6">Enter a company domain above to analyze their target personas.</p>
          <button
            onClick={handleUseSample}
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Try Sample Analysis
          </button>
        </div>
      )}
    </div>
  );
};

export default Companies;
