import React, { useState, useEffect } from 'react';
import { Building2, ExternalLink, Plus, Search, Filter, Trash2, X } from 'lucide-react';
import { apiService } from '../services/api';
import toast from 'react-hot-toast';

const Companies = () => {
  const [companies, setCompanies] = useState([]);
  const [accountCompany, setAccountCompany] = useState(null);
  const [loading, setLoading] = useState(true);
  const [accountLoading, setAccountLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterAnalyzed, setFilterAnalyzed] = useState('all');
  const [linkedinUrl, setLinkedinUrl] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [domData, setDomData] = useState('');

  const handleAddCompany = async () => {
    if (!linkedinUrl.trim()) {
      toast.error('Please enter a LinkedIn company URL');
      return;
    }

    let parsedDomData = {};

    // If DOM data is provided, try to parse it
    if (domData.trim()) {
      try {
        // Try to parse as JSON first
        parsedDomData = JSON.parse(domData);
      } catch (e) {
        // If not JSON, try to extract data from HTML/text
        parsedDomData = extractDomData(domData);
      }
    } else {
      // Fallback: extract company name from URL
      const urlMatch = linkedinUrl.match(/linkedin\.com\/company\/([^\/\?]+)/);
      const companySlug = urlMatch ? urlMatch[1] : 'unknown-company';
      parsedDomData = {
        company_name: companySlug.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
        description: 'Company added from LinkedIn URL',
        linkedin_url: linkedinUrl
      };
    }

    try {
      await apiService.saveCompanyFromLinkedIn({
        linkedinUrl,
        domData: parsedDomData
      });

      setLinkedinUrl('');
      setDomData('');
      setShowAddForm(false);
      loadCompanies();
      toast.success('Company added successfully');
    } catch (error) { 
      console.error('Failed to add company:', error);
      toast.error('Failed to add company');
    }
  };

  const extractDomData = (htmlText) => {
    // Helper function to extract company data from HTML/text
    const data = {};

    // Try to extract company name
    const nameMatch = htmlText.match(/<h1[^>]*>([^<]+)<\/h1>/) || 
                     htmlText.match(/company[_-]?name['":\s]*([^'",\n]+)/i);
    if (nameMatch) data.company_name = nameMatch[1].trim();

    // Try to extract description
    const descMatch = htmlText.match(/<meta[^>]*description[^>]*content[='"]\s*([^'"]+)/i) ||
                     htmlText.match(/description['":\s]*([^'",\n]{20,})/i);
    if (descMatch) data.description = descMatch[1].trim();

    // Try to extract industry
    const industryMatch = htmlText.match(/industry['":\s]*([^'",\n]+)/i);
    if (industryMatch) data.industry = industryMatch[1].trim();

    // Try to extract size
    const sizeMatch = htmlText.match(/size['":\s]*([^'",\n]+)/i) ||
                     htmlText.match(/employees['":\s]*([^'",\n]+)/i);
    if (sizeMatch) data.size = sizeMatch[1].trim();

    // Try to extract location
    const locationMatch = htmlText.match(/location['":\s]*([^'",\n]+)/i) ||
                         htmlText.match(/headquarters['":\s]*([^'",\n]+)/i);
    if (locationMatch) data.location = locationMatch[1].trim();

    return data;
  };

  useEffect(() => {
    loadAccountCompany();
    loadCompanies();
  }, []);

  const loadAccountCompany = async () => {
    try {
      const response = await apiService.getAccountCompany();
      setAccountCompany(response.data.company);
    } catch (error) {
      console.error('Failed to load account company:', error);
      toast.error('Failed to load account company');
    } finally {
      setAccountLoading(false);
    }
  };

  const loadCompanies = async () => {
    try {
      const response = await apiService.getProspectCompanies();
      setCompanies(response.data.companies);
    } catch (error) {
      console.error('Failed to load companies:', error);
      toast.error('Failed to load companies');
    } finally {
      setLoading(false);
    }
  };

  const handleAnalyzeAccount = async () => {
    if (!accountCompany) return;

    try {
      await apiService.analyzeCompanyInfo(accountCompany.domain);
      await loadAccountCompany(); // Reload to get updated analysis
      toast.success('Account company analyzed successfully');
    } catch (error) {
      console.error('Failed to analyze account company:', error);
      toast.error('Failed to analyze account company');
    }
  };

  const handleDeleteCompany = async (domain) => {
    if (!window.confirm('Are you sure you want to delete this company and all its prospects?')) {
      return;
    }

    try {
      await apiService.deleteCompany(domain);
      setCompanies(companies.filter(c => c.domain !== domain));
      toast.success('Company deleted successfully');
    } catch (error) {
      console.error('Failed to delete company:', error);
      toast.error('Failed to delete company');
    }
  };

  const filteredCompanies = companies.filter(company => {
    const matchesSearch = company.domain.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         company.analysis_data?.company_name?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesFilter = filterAnalyzed === 'all' ||
                         (filterAnalyzed === 'analyzed' && company.analysis_data && Object.keys(company.analysis_data).length > 0) ||
                         (filterAnalyzed === 'unanalyzed' && (!company.analysis_data || Object.keys(company.analysis_data).length === 0));

    return matchesSearch && matchesFilter;
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Company Prospecting</h1>
          <p className="text-gray-600 mt-1">
            Analyze your company first, then add prospect companies to target
          </p>
        </div>  
        <div className="mt-4 sm:mt-0">
          <button 
            onClick={() => setShowAddForm(!showAddForm)} 
            className="btn-primary"
          >
            <Plus className="h-4 w-4 mr-2" />
            Add Prospect Company
          </button>
        </div>
      </div>  

      {/* Account Company Analysis Section */}
      <div className="card p-6 bg-blue-50 border border-blue-200">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-lg font-semibold text-gray-900 mb-1">
              Your Account Company Analysis
            </h2>
            <p className="text-sm text-gray-600">
              First, analyze your own company to help AI understand your business for better prospect targeting
            </p>
          </div>
        </div>

        {accountLoading ? (
          <div className="flex items-center justify-center h-24">
            <div className="spinner"></div>
          </div>
        ) : accountCompany ? (
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="font-medium text-gray-900">
                  {accountCompany.analysis_data?.company_name || accountCompany.domain}
                </h3>
                <p className="text-sm text-gray-500">{accountCompany.domain}</p>
              </div>
              <div className="flex items-center space-x-2">
                {accountCompany.analysis_data && Object.keys(accountCompany.analysis_data).length > 0 ? (
                  <span className="px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                    ✓ Analyzed
                  </span>
                ) : (
                  <button
                    onClick={handleAnalyzeAccount}
                    className="btn-primary text-sm"
                  >
                    Analyze My Company
                  </button>
                )}
              </div>
            </div>

            {accountCompany.analysis_data && (
              <div className="bg-white rounded-lg p-4 space-y-2">
                {accountCompany.analysis_data.industry && (
                  <p className="text-sm text-gray-600">
                    <span className="font-medium">Industry:</span> {accountCompany.analysis_data.industry}
                  </p>
                )}
                {accountCompany.analysis_data.description && (
                  <p className="text-sm text-gray-600">
                    <span className="font-medium">Description:</span> {accountCompany.analysis_data.description}
                  </p>
                )}
                {accountCompany.analysis_data.target_markets && accountCompany.analysis_data.target_markets.length > 0 && (
                  <p className="text-sm text-gray-600">
                    <span className="font-medium">Target Markets:</span> {accountCompany.analysis_data.target_markets.join(', ')}
                  </p>
                )}
              </div>
            )}
          </div>
        ) : (
          <div className="text-center py-8">
            <Building2 className="h-12 w-12 mx-auto mb-3 text-gray-400" />
            <p className="text-gray-600">No account company found</p>
          </div>
        )}
      </div>

      {/* Prospect Companies Section */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold text-gray-900">Prospect Companies</h2>
          <p className="text-gray-600 mt-1">
            Companies you want to prospect and find people at
          </p>
        </div>
      </div>

      {/* Filters */}
      <div className="card p-4">
        <div className="flex flex-col sm:flex-row sm:items-center space-y-4 sm:space-y-0 sm:space-x-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search companies..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input pl-10 w-full"
              />
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <Filter className="h-4 w-4 text-gray-400" />
            <select
              value={filterAnalyzed}
              onChange={(e) => setFilterAnalyzed(e.target.value)}
              className="input"
            >
              <option value="all">All Companies</option>
              <option value="analyzed">Analyzed</option>
              <option value="unanalyzed">Not Analyzed</option>
            </select>
          </div>
        </div>
      </div>

      {/* Companies Grid */}
      {filteredCompanies.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredCompanies.map((company) => (
            <div key={company.id} className="card p-6 hover:shadow-lg transition-shadow">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mr-3">
                    <Building2 className="h-6 w-6 text-blue-600" />
                  </div>
                  <div>
                    <h3 className="font-semibold text-gray-900">
                      {company.analysis_data?.company_name || company.domain}
                    </h3>
                    <p className="text-sm text-gray-500">{company.domain}</p>
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  {company.linkedin_url && (
                    <a
                      href={company.linkedin_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-gray-400 hover:text-gray-600"
                    >
                      <ExternalLink className="h-4 w-4" />
                    </a>
                  )}
                  <button
                    onClick={() => handleDeleteCompany(company.domain)}
                    className="text-gray-400 hover:text-red-600"
                  >
                    <Trash2 className="h-4 w-4" />
                  </button>
                </div>
              </div>

              {company.analysis_data && (
                <div className="space-y-2 mb-4">
                  {company.analysis_data.industry && (
                    <p className="text-sm text-gray-600">
                      <span className="font-medium">Industry:</span> {company.analysis_data.industry}
                    </p>
                  )}
                  {company.analysis_data.size && (
                    <p className="text-sm text-gray-600">
                      <span className="font-medium">Size:</span> {company.analysis_data.size}
                    </p>
                  )}
                  {company.analysis_data.location && (
                    <p className="text-sm text-gray-600">
                      <span className="font-medium">Location:</span> {company.analysis_data.location}
                    </p>
                  )}
                </div>
              )}

              <div className="flex items-center justify-between">
                <div className="flex space-x-2">
                  {company.analysis_data && Object.keys(company.analysis_data).length > 0 ? (
                    <span className="px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                      Analyzed
                    </span>
                  ) : (
                    <span className="px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                      Pending Analysis
                    </span>
                  )}

                  {company.personas_data && Object.keys(company.personas_data).length > 0 && (
                    <span className="px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                      Personas Ready
                    </span>
                  )}
                </div>


              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="card p-12">
          <div className="text-center">
            <Building2 className="h-16 w-16 mx-auto mb-4 text-gray-300" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {searchTerm || filterAnalyzed !== 'all' ? 'No prospect companies found' : 'No prospect companies yet'}
            </h3>
            <p className="text-gray-600 mb-6">
              {searchTerm || filterAnalyzed !== 'all' 
                ? 'Try adjusting your search or filters'
                : 'Add LinkedIn company URLs above or install the Chrome extension to scrape company data automatically'
              }
            </p>
            <a
              href="https://linkedin.com/company"
              target="_blank"
              rel="noopener noreferrer"
              className="btn-primary inline-flex items-center"
            >
              <Plus className="h-4 w-4 mr-2" />
              Find Companies on LinkedIn
            </a>
          </div>
        </div>
      )}

      {/* Chrome Extension CTA */}
      {companies.length === 0 && (
        <div className="card p-6 bg-blue-50 border-blue-200">
          <div className="flex items-start">
            <div className="bg-blue-100 p-2 rounded-lg mr-4 mt-1">
              <Building2 className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900 mb-1">
                Install Chrome Extension
              </h3>
              <p className="text-gray-600 mb-3">
                Get the Linkify Chrome extension to automatically analyze companies 
                and prospects while browsing LinkedIn.
              </p>
              <button className="btn-primary text-sm">
                Install Extension
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Companies;
