import React, { useState, useEffect } from 'react';
import { User, Target, TrendingUp, MessageCircle, Lightbulb, Star, MapPin, GraduationCap, Briefcase } from 'lucide-react';
import { apiService } from '../services/api';
import toast from 'react-hot-toast';

const People = () => {
  const [loading, setLoading] = useState(false);
  const [analysis, setAnalysis] = useState(null);

  const samplePeopleData = {
    profile: {
      name: "Srikrishnan Ganesan",
      linkedin_url: "https://www.linkedin.com/in/srikrishnang/",
      connection_degree: "2nd degree connection",
      headline: "#1 Professional Services Automation, Project Delivery, and Client Onboarding Software. Rocketlane is a purpose-built client-centric PSA tool for implementation teams, consulting firms, and agencies.",
      company: "Rocketlane",
      education: "Indian Institute of Management, Bangalore",
      location: "San Francisco, California, United States",
      contact_info_url: "https://www.linkedin.com/in/srikrishnang/overlay/contact-info/",
      followers: 30133,
      connections: "500+",
      mutual_connections: {
        count: 7,
        examples: ["Arihant Jain", "Deepak Bhilala"],
        url: "https://www.linkedin.com/search/results/people/?facetNetwork=%22F%22&facetConnectionOf=%22ACoAAAC0kZMBsyGTkhNu1iWuETGlZuJ2OiWEN2Q%22&origin=MEMBER_PROFILE_CANNED_SEARCH"
      },
      about: "Crafting Rocketlane, & building the Preflight community (https://preflight.cx). Rocketlane is a purpose-built customer onboarding platform that shortens your time to value, and eliminates hit-or-miss experiences for your customers.",
      experience: [
        {
          company: "Rocketlane",
          duration: "5 yrs 5 mos",
          period: "Apr 2020 - Present",
          position: "Co-Founder & CEO",
          employment_type: "Full-time",
          description: "Rocketlane is a purpose-built customer onboarding platform that shortens your time to value and eliminates hit-or-miss experiences for customers."
        }
      ]
    }
  };

  const handleAnalyze = async () => {
    setLoading(true);
    try {
      const response = await apiService.analyzePeople(samplePeopleData);
      setAnalysis(response.data);
      toast.success('Analysis completed successfully');
    } catch (error) {
      console.error('Analysis error:', error);
      toast.error('Failed to analyze person');
    } finally {
      setLoading(false);
    }
  };

  // Listen for profile data from the Chrome extension side panel
  useEffect(() => {
    const handleWindowMessage = async (event) => {
      const payload = event.data;
      if (payload?.type === 'LINKIFY_PROFILE_DATA' && payload?.data) {
        setLoading(true);
        try {
          const peopledata = { profile: payload.data };

          console.log('peopledata', peopledata);


          const response = await apiService.analyzePeople(peopledata);
          setAnalysis(response.data);
          toast.success('Profile analyzed successfully');
        } catch (error) {
          console.error('Profile analysis error:', error);
          toast.error('Failed to analyze profile');
        } finally {
          setLoading(false);
        }
      }
    };

    window.addEventListener('message', handleWindowMessage);
    return () => window.removeEventListener('message', handleWindowMessage);
  }, []);

  const handleUseSample = () => {
    setLoading(true);
    setTimeout(() => {
      setAnalysis({
        data: {
          personas: {
            personas: [
              {
                type: "decision_maker",
                title: "VP of Professional Services",
                department: "Professional Services",
                seniority_level: "VP/Director",
                typical_responsibilities: [
                  "Oversee delivery of client onboarding and implementation projects",
                  "Manage resource allocation and project profitability",
                  "Select and implement tools for project management and automation"
                ],
                pain_points: [
                  "Lack of visibility into project status and resource utilization",
                  "Difficulty delivering projects on time and within budget",
                  "Manual, fragmented workflows causing inefficiency"
                ],
                influence_level: "high",
                budget_authority: true,
                linkedin_search_terms: [
                  "VP Professional Services",
                  "Director Professional Services",
                  "Head of Professional Services"
                ]
              }
            ],
            targeting_strategy: "Prioritize outreach to VP/Director-level leaders in Professional Services and Customer Success, as they hold decision-making and sponsorship authority for onboarding and PSA software.",
            recommended_approach: "Leverage a consultative sales approach rooted in first-principles thinking to uncover root causes of onboarding and delivery inefficiencies."
          }
        },
        people: {
          PersonDetails: {
            name: "Srikrishnan Ganesan",
            title: "Co-Founder & CEO",
            company: "Rocketlane",
            location: "San Francisco, California, United States",
            experience_years: 13,
            education: "Indian Institute of Management, Bangalore",
            skills: [
              "Professional Services Automation",
              "Customer Onboarding",
              "Project Delivery",
              "SaaS Product Management",
              "Startup Leadership",
              "Community Building"
            ]
          },
          PersonaType: {
            type: "decision_maker",
            confidence: "high",
            reasoning: "As Co-Founder & CEO of Rocketlane, Srikrishnan is the primary decision maker for technology adoption, partnerships, and strategic direction."
          },
          PersonalityTraits: {
            communication_style: "Transparent, community-driven, and narrative-focused; leverages storytelling and public engagement.",
            decision_making_style: "Data-informed, customer-centric, and innovative; prioritizes measurable outcomes and rapid iteration.",
            key_motivators: [
              "Delivering superior customer onboarding experiences",
              "Building category-defining products",
              "Driving measurable impact for clients"
            ],
            potential_objections: [
              "Skepticism toward solutions lacking clear differentiation or ROI",
              "Preference for platforms that integrate seamlessly with existing workflows"
            ]
          },
          ICP_FitScore: {
            score: 10,
            max_score: 10,
            reasoning: "Srikrishnan is the CEO of a company that builds PSA and onboarding software, directly matching the ideal customer profile.",
            fit_factors: [
              "Executive authority over technology and business strategy",
              "Direct alignment with PSA, onboarding, and professional services domains",
              "Demonstrated history of SaaS product innovation and adoption"
            ]
          },
          OutreachPlan: {
            recommended_approach: "Adopt a peer-to-peer, consultative approach focused on thought leadership and industry trends.",
            key_talking_points: [
              "Trends and challenges in professional services automation",
              "Innovative approaches to customer onboarding and implementation",
              "Quantifiable outcomes from unified PSA platforms"
            ],
            best_contact_method: "LinkedIn direct message, referencing mutual connections or recent viral posts.",
            timing_recommendations: "Engage shortly after major product announcements, viral posts, or community events."
          },
          AdditionalInsights: {
            mutual_connections: ["Arihant Jain", "Deepak Bhilala"],
            shared_interests: [
              "Customer onboarding best practices",
              "SaaS product leadership",
              "Community building in tech"
            ],
            conversation_starters: [
              "Congratulate on recent viral Series A post and discuss content-driven community engagement.",
              "Reference Preflight community initiatives and explore collaboration."
            ],
            red_flags: [
              "As CEO of a direct competitor in PSA/onboarding, may not be a buyer but rather a peer or potential partner."
            ]
          },
          personalize_message_to_reach_out: "Hi Srikrishnan, I was impressed by Rocketlane's recent Series A campaign and your leadership in building the Preflight community. Your insights on onboarding excellence and customer-centric PSA resonate with our approach to driving measurable outcomes for implementation teams.",
          email_to_reach_out: ""
        },
        fromCache: false
      });
      setLoading(false);
      toast.success('Sample analysis loaded');
    }, 1000);
  };

  const getPersonaTypeColor = (type) => {
    const colors = {
      decision_maker: 'bg-red-100 text-red-800',
      sponsor: 'bg-blue-100 text-blue-800',
      influencer: 'bg-yellow-100 text-yellow-800',
      champion: 'bg-green-100 text-green-800'
    };
    return colors[type] || 'bg-gray-100 text-gray-800';
  };

  const getScoreColor = (score, maxScore) => {
    const percentage = (score / maxScore) * 100;
    if (percentage >= 80) return 'text-green-600';
    if (percentage >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <div className="max-w-7xl mx-auto space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex items-center mb-4">
          <User className="h-8 w-8 text-blue-600 mr-3" />
          <h1 className="text-2xl font-bold text-gray-900">People Analysis</h1>
        </div>
        <p className="text-gray-600 mb-6">
          Analyze LinkedIn profiles to understand personas, fit scores, and create personalized outreach strategies.
        </p>

        <div className="flex gap-4">
          <button
            onClick={handleAnalyze}
            disabled={loading}
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
          >
            {loading ? 'Analyzing...' : 'Analyze Sample Profile'}
          </button>
          <button
            onClick={handleUseSample}
            disabled={loading}
            className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
          >
            Load Sample Data
          </button>
        </div>
      </div>

      {/* Results */}
      {analysis && analysis.people && (
        <div className="space-y-6">
          {/* Person Details */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Person Details</h2>

            <div className="flex items-start space-x-6">
              <div className="bg-blue-100 rounded-full p-4">
                <User className="h-12 w-12 text-blue-600" />
              </div>

              <div className="flex-1">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">{analysis.people.PersonDetails.name}</h3>
                    <p className="text-gray-600 mb-2">{analysis.people.PersonDetails.title}</p>

                    <div className="space-y-2">
                      <div className="flex items-center text-sm text-gray-600">
                        <Briefcase className="h-4 w-4 mr-2" />
                        {analysis.people.PersonDetails.company}
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <MapPin className="h-4 w-4 mr-2" />
                        {analysis.people.PersonDetails.location}
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <GraduationCap className="h-4 w-4 mr-2" />
                        {analysis.people.PersonDetails.education}
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <TrendingUp className="h-4 w-4 mr-2" />
                        {analysis.people.PersonDetails.experience_years} years experience
                      </div>
                    </div>
                  </div>

                  <div>
                    <h4 className="text-sm font-medium text-gray-900 mb-3">Key Skills</h4>
                    <div className="flex flex-wrap gap-2">
                      {analysis.people.PersonDetails.skills?.map((skill, idx) => (
                        <span key={idx} className="inline-flex px-2 py-1 text-xs bg-blue-100 text-blue-800 rounded-md">
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Persona Type & ICP Fit */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Persona Classification</h2>

              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-600">Type</span>
                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getPersonaTypeColor(analysis.people.PersonaType.type)}`}>
                    {analysis.people.PersonaType.type.replace('_', ' ').toUpperCase()}
                  </span>
                </div>

                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-600">Confidence</span>
                  <span className="text-sm font-semibold text-green-600">{analysis.people.PersonaType.confidence}</span>
                </div>

                <div>
                  <span className="text-sm font-medium text-gray-600">Reasoning</span>
                  <p className="text-sm text-gray-800 mt-1">{analysis.people.PersonaType.reasoning}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">ICP Fit Score</h2>

              <div className="text-center mb-4">
                <div className={`text-3xl font-bold ${getScoreColor(analysis.people.ICP_FitScore.score, analysis.people.ICP_FitScore.max_score)}`}>
                  {analysis.people.ICP_FitScore.score}/{analysis.people.ICP_FitScore.max_score}
                </div>
                <div className="text-sm text-gray-600 mt-1">Perfect Match</div>
              </div>

              <div>
                <p className="text-sm text-gray-800 mb-3">{analysis.people.ICP_FitScore.reasoning}</p>

                <h4 className="text-sm font-medium text-gray-900 mb-2">Fit Factors</h4>
                <ul className="text-sm text-gray-600 space-y-1">
                  {analysis.people.ICP_FitScore.fit_factors?.map((factor, idx) => (
                    <li key={idx} className="flex items-start">
                      <span className="block w-1 h-1 bg-green-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                      {factor}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>

          {/* Personality & Communication */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Personality Profile</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <h4 className="text-sm font-medium text-gray-900 mb-3">Communication Style</h4>
                <p className="text-sm text-gray-800 mb-4">{analysis.people.PersonalityTraits.communication_style}</p>

                <h4 className="text-sm font-medium text-gray-900 mb-3">Decision Making Style</h4>
                <p className="text-sm text-gray-800">{analysis.people.PersonalityTraits.decision_making_style}</p>
              </div>

              <div>
                <h4 className="text-sm font-medium text-gray-900 mb-3">Key Motivators</h4>
                <ul className="text-sm text-gray-600 space-y-1 mb-4">
                  {analysis.people.PersonalityTraits.key_motivators?.map((motivator, idx) => (
                    <li key={idx} className="flex items-start">
                      <span className="block w-1 h-1 bg-green-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                      {motivator}
                    </li>
                  ))}
                </ul>

                <h4 className="text-sm font-medium text-gray-900 mb-3">Potential Objections</h4>
                <ul className="text-sm text-gray-600 space-y-1">
                  {analysis.people.PersonalityTraits.potential_objections?.map((objection, idx) => (
                    <li key={idx} className="flex items-start">
                      <span className="block w-1 h-1 bg-red-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                      {objection}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>

          {/* Outreach Plan */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Outreach Strategy</h2>

            <div className="space-y-6">
              <div className="bg-blue-50 rounded-lg p-4">
                <div className="flex items-start">
                  <Target className="h-5 w-5 text-blue-600 mt-0.5 mr-3 flex-shrink-0" />
                  <div>
                    <h3 className="font-medium text-gray-900 mb-2">Recommended Approach</h3>
                    <p className="text-gray-800">{analysis.people.OutreachPlan.recommended_approach}</p>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-3">Key Talking Points</h4>
                  <ul className="text-sm text-gray-600 space-y-1">
                    {analysis.people.OutreachPlan.key_talking_points?.map((point, idx) => (
                      <li key={idx} className="flex items-start">
                        <span className="block w-1 h-1 bg-blue-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                        {point}
                      </li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-3">Contact Method</h4>
                  <p className="text-sm text-gray-800 mb-3">{analysis.people.OutreachPlan.best_contact_method}</p>

                  <h4 className="text-sm font-medium text-gray-900 mb-3">Timing</h4>
                  <p className="text-sm text-gray-800">{analysis.people.OutreachPlan.timing_recommendations}</p>
                </div>
              </div>

              <div className="bg-green-50 rounded-lg p-4">
                <div className="flex items-start">
                  <MessageCircle className="h-5 w-5 text-green-600 mt-0.5 mr-3 flex-shrink-0" />
                  <div>
                    <h3 className="font-medium text-gray-900 mb-2">Personalized Message</h3>
                    <p className="text-gray-800 italic">"{analysis.people.personalize_message_to_reach_out}"</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Additional Insights */}
          {analysis.people.AdditionalInsights && (
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-6">Additional Insights</h2>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-3">Mutual Connections</h4>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {analysis.people.AdditionalInsights.mutual_connections?.map((connection, idx) => (
                      <span key={idx} className="inline-flex px-2 py-1 text-xs bg-gray-100 text-gray-800 rounded-md">
                        {connection}
                      </span>
                    ))}
                  </div>

                  <h4 className="text-sm font-medium text-gray-900 mb-3">Shared Interests</h4>
                  <div className="flex flex-wrap gap-2">
                    {analysis.people.AdditionalInsights.shared_interests?.map((interest, idx) => (
                      <span key={idx} className="inline-flex px-2 py-1 text-xs bg-green-100 text-green-800 rounded-md">
                        {interest}
                      </span>
                    ))}
                  </div>
                </div>

                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-3">Conversation Starters</h4>
                  <ul className="text-sm text-gray-600 space-y-1 mb-4">
                    {analysis.people.AdditionalInsights.conversation_starters?.map((starter, idx) => (
                      <li key={idx} className="flex items-start">
                        <span className="block w-1 h-1 bg-blue-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                        {starter}
                      </li>
                    ))}
                  </ul>

                  <h4 className="text-sm font-medium text-gray-900 mb-3">Red Flags</h4>
                  <ul className="text-sm text-gray-600 space-y-1">
                    {analysis.people.AdditionalInsights.red_flags?.map((flag, idx) => (
                      <li key={idx} className="flex items-start">
                        <span className="block w-1 h-1 bg-red-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                        {flag}
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Empty State */}
      {!analysis && !loading && (
        <div className="bg-white rounded-lg shadow-sm p-12 text-center">
          <User className="h-16 w-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Analysis Yet</h3>
          <p className="text-gray-600 mb-6">Analyze a LinkedIn profile to get detailed persona insights and outreach recommendations.</p>
          <button
            onClick={handleUseSample}
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Try Sample Analysis
          </button>
        </div>
      )}
    </div>
  );
};

export default People;
