import React, { useState, useContext } from 'react';
import { Building2, Users, Target, TrendingUp, Lightbulb, ExternalLink, Globe } from 'lucide-react';
import { apiService } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import toast from 'react-hot-toast';

const Companies = () => {
  const [loading, setLoading] = useState(false);
  const [analysis, setAnalysis] = useState(null);
  const [linkedinUrl, setLinkedinUrl] = useState('');
  const { user } = useAuth();

  const handleAnalyze = async () => {
    if (!linkedinUrl.trim()) {
      toast.error('Please enter a LinkedIn company URL');
      return;
    }

    if (!user?.domain) {
      toast.error('Account domain not found. Please check your profile settings.');
      return;
    }

    setLoading(true);
    try {
      const response = await apiService.analyzeCompanyPersonasNew(linkedinUrl, user.domain);
      console.log('API Response:', response.data);
      setAnalysis(response.data);
      toast.success('Analysis completed successfully');
    } catch (error) {
      console.error('Analysis error:', error);
      toast.error('Failed to analyze company personas');
    } finally {
      setLoading(false);
    }
  };

  const getPersonaTypeColor = (type) => {
    const colors = {
      decision_maker: 'bg-red-100 text-red-800',
      sponsor: 'bg-blue-100 text-blue-800',
      influencer: 'bg-yellow-100 text-yellow-800',
      champion: 'bg-green-100 text-green-800',
      budget_holder: 'bg-purple-100 text-purple-800',
      end_user: 'bg-gray-100 text-gray-800'
    };
    return colors[type] || 'bg-gray-100 text-gray-800';
  };

  const getInfluenceLevelColor = (level) => {
    const colors = {
      high: 'text-red-600',
      'high': 'text-red-600',
      medium: 'text-yellow-600',
      'medium-high': 'text-orange-600',
      low: 'text-green-600'
    };
    return colors[level] || 'text-gray-600';
  };

  const parseAnalysisData = (analysisDataString) => {
    try {
      if (!analysisDataString || typeof analysisDataString !== 'string') {
        console.log('Invalid input for parsing:', analysisDataString);
        return null;
      }

      console.log('Attempting to parse:', analysisDataString.substring(0, 200) + '...');

      // First, try to parse as-is in case it's already valid JSON
      try {
        const result = JSON.parse(analysisDataString);
        console.log('Direct parsing successful');
        return result;
      } catch (e) {
        console.log('Direct parsing failed, trying to clean string');
      }

      // Remove escaped quotes and outer quotes
      let cleanedString = analysisDataString
        .replace(/\\"/g, '"')  // Replace \" with "
        .replace(/^"|"$/g, '') // Remove outer quotes
        .replace(/\\n/g, ' ')  // Replace \n with space
        .replace(/\\t/g, ' ')  // Replace \t with space
        .replace(/\\r/g, ' '); // Replace \r with space

      console.log('Cleaned string (first 200 chars):', cleanedString.substring(0, 200) + '...');

      // Try to parse the cleaned string
      const result = JSON.parse(cleanedString);
      console.log('Cleaned parsing successful');
      return result;
    } catch (error) {
      console.error('Error parsing analysis data:', error);
      console.error('Original string (first 500 chars):', analysisDataString.substring(0, 500));
      return null;
    }
  };

  return (
    <div className="max-w-7xl mx-auto space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex items-center mb-4">
          <Building2 className="h-8 w-8 text-blue-600 mr-3" />
          <h1 className="text-2xl font-bold text-gray-900">Company Persona Analysis</h1>
        </div>
        <p className="text-gray-600 mb-6">
          Analyze LinkedIn company profiles to identify key decision-makers and build targeted outreach strategies.
        </p>

        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <label htmlFor="linkedinUrl" className="block text-sm font-medium text-gray-700 mb-2">
              LinkedIn Company URL
            </label>
            <input
              type="url"
              id="linkedinUrl"
              value={linkedinUrl}
              onChange={(e) => setLinkedinUrl(e.target.value)}
              placeholder="https://www.linkedin.com/company/company-name/"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="flex gap-2 items-end">
            <button
              onClick={handleAnalyze}
              disabled={loading || !user?.domain}
              className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
            >
              {loading ? 'Analyzing...' : 'Analyze'}
            </button>
          </div>
        </div>

        {!user?.domain && (
          <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded-md">
            <p className="text-sm text-yellow-800">
              Account domain not configured. Please update your profile settings.
            </p>
          </div>
        )}
      </div>

            {/* Results */}
      {analysis && analysis.company && (() => {
        const parsedData = parseAnalysisData(analysis.company.analysis_data);
        console.log('Parsed data for UI:', parsedData);
        console.log('Company information:', parsedData?.company_information);
        console.log('Personas:', parsedData?.personas);
        console.log('Ideal customer personas:', parsedData?.company_information?.ideal_customer_personas);

        return (
          <div className="space-y-6">


          {/* Target Company Information */}
          {parsedData?.company_information && (
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Target Company Information</h2>
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-3">Company Details</h3>
                  <div className="space-y-3">
                    <div>
                      <span className="text-sm font-medium text-gray-600">Industry:</span>
                      <p className="text-gray-900">{parsedData.company_information.industry || 'N/A'}</p>
                    </div>
                    <div>
                      <span className="text-sm font-medium text-gray-600">Size:</span>
                      <p className="text-gray-900">{parsedData.company_information.size || 'N/A'}</p>
                    </div>
                    <div>
                      <span className="text-sm font-medium text-gray-600">Location:</span>
                      <p className="text-gray-900">{parsedData.company_information.location || 'N/A'}</p>
                    </div>
                    <div>
                      <span className="text-sm font-medium text-gray-600">Description:</span>
                      <p className="text-gray-900 text-sm">{parsedData.company_information.description || 'N/A'}</p>
                    </div>
                  </div>
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-3">Business Information</h3>
                  <div className="space-y-3">
                    <div>
                      <span className="text-sm font-medium text-gray-600">Business Model:</span>
                      <p className="text-gray-900 text-sm">{parsedData.company_information.business_model || 'N/A'}</p>
                    </div>
                    <div>
                      <span className="text-sm font-medium text-gray-600">Revenue Model:</span>
                      <p className="text-gray-900 text-sm">{parsedData.company_information.revenue_model || 'N/A'}</p>
                    </div>
                    <div>
                      <span className="text-sm font-medium text-gray-600">Growth Stage:</span>
                      <p className="text-gray-900 text-sm">{parsedData.company_information.growth_stage || 'N/A'}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Personas */}
          {parsedData?.personas && (
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-6">Target Personas</h2>
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {parsedData.personas.map((persona, index) => (
                  <div key={index} className="border border-gray-200 rounded-lg p-6">
                    <div className="flex items-start justify-between mb-4">
                      <div>
                        <div className="flex items-center mb-2">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getPersonaTypeColor(persona.type)}`}>
                            {persona.type.replace('_', ' ').toUpperCase()}
                          </span>
                          <span className={`ml-2 text-sm font-medium ${getInfluenceLevelColor(persona.influence_level)}`}>
                            {persona.influence_level} influence
                          </span>
                        </div>
                        <h3 className="text-lg font-semibold text-gray-900">{persona.title}</h3>
                        <p className="text-sm text-gray-600">{persona.department} â€¢ {persona.seniority_level}</p>
                        {persona.budget_authority && persona.budget_authority !== "No" && (
                          <span className="inline-flex items-center px-2 py-1 text-xs font-medium bg-green-100 text-green-800 rounded-full mt-2">
                            Budget Authority
                          </span>
                        )}
                      </div>
                    </div>

                    <div className="space-y-4">
                      <div>
                        <h4 className="text-sm font-medium text-gray-900 mb-2">Responsibilities</h4>
                        <ul className="text-sm text-gray-600 space-y-1">
                          {persona.typical_responsibilities?.slice(0, 3).map((resp, idx) => (
                            <li key={idx} className="flex items-start">
                              <span className="block w-1 h-1 bg-gray-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                              {resp}
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div>
                        <h4 className="text-sm font-medium text-gray-900 mb-2">Pain Points</h4>
                        <ul className="text-sm text-gray-600 space-y-1">
                          {persona.pain_points?.slice(0, 3).map((pain, idx) => (
                            <li key={idx} className="flex items-start">
                              <span className="block w-1 h-1 bg-red-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                              {pain}
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div>
                        <h4 className="text-sm font-medium text-gray-900 mb-2">LinkedIn Search Terms</h4>
                        <div className="flex flex-wrap gap-2">
                          {persona.linkedin_search_terms?.slice(0, 3).map((term, idx) => (
                            <span key={idx} className="inline-flex px-2 py-1 text-xs bg-blue-100 text-blue-800 rounded-md">
                              {term}
                            </span>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Account Company Information */}
          {analysis.company.account_company_data && (
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Your Account Company</h2>
              <div className="bg-blue-50 rounded-lg p-6">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h3 className="text-lg font-medium text-gray-900 mb-3">Company Details</h3>
                    <div className="space-y-3">
                      <div>
                        <span className="text-sm font-medium text-gray-600">Company Name:</span>
                        <p className="text-gray-900">{analysis.company.account_company_data.analysis_data.company_name}</p>
                      </div>
                      <div>
                        <span className="text-sm font-medium text-gray-600">Industry:</span>
                        <p className="text-gray-900">{analysis.company.account_company_data.analysis_data.industry}</p>
                      </div>
                      <div>
                        <span className="text-sm font-medium text-gray-600">Description:</span>
                        <p className="text-gray-900 text-sm">{analysis.company.account_company_data.analysis_data.description}</p>
                      </div>
                    </div>
                  </div>
                  <div>
                    <h3 className="text-lg font-medium text-gray-900 mb-3">Business Information</h3>
                    <div className="space-y-3">
                      <div>
                        <span className="text-sm font-medium text-gray-600">Business Model:</span>
                        <p className="text-gray-900 text-sm">{analysis.company.account_company_data.analysis_data.business_model}</p>
                      </div>
                      <div>
                        <span className="text-sm font-medium text-gray-600">Revenue Model:</span>
                        <p className="text-gray-900 text-sm">{analysis.company.account_company_data.analysis_data.revenue_model}</p>
                      </div>
                      <div>
                        <span className="text-sm font-medium text-gray-600">Growth Stage:</span>
                        <p className="text-gray-900 text-sm">{analysis.company.account_company_data.analysis_data.growth_stage}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
        );
      })()}

      {/* Empty State */}
      {!analysis && !loading && (
        <div className="bg-white rounded-lg shadow-sm p-12 text-center">
          <Building2 className="h-16 w-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Analysis Yet</h3>
          <p className="text-gray-600 mb-6">Enter a LinkedIn company URL above to analyze their target personas.</p>
        </div>
      )}
    </div>
  );
};

export default Companies;
