import React, { useState, useEffect } from 'react';
import { Users, ExternalLink, Search, Star, Trash2, Edit3 } from 'lucide-react';
import { apiService } from '../services/api';
import toast from 'react-hot-toast';

const Prospects = () => {
  const [prospects, setProspects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    status: 'all',
    persona_match: 'all',
    is_ideal_contact: 'all',
    score_min: ''
  });
  const [stats, setStats] = useState(null);
  const [editingProspect, setEditingProspect] = useState(null);

  useEffect(() => {
    loadProspects();
    loadStats();
  }, [filters]);

  const loadProspects = async () => {
    try {
      const queryParams = {};
      if (filters.status !== 'all') queryParams.status = filters.status;
      if (filters.persona_match !== 'all') queryParams.persona_match = filters.persona_match;
      if (filters.is_ideal_contact !== 'all') queryParams.is_ideal_contact = filters.is_ideal_contact;
      if (filters.score_min) queryParams.score_min = filters.score_min;

      const response = await apiService.getProspects(queryParams);
      setProspects(response.data.prospects);
    } catch (error) {
      console.error('Failed to load prospects:', error);
      toast.error('Failed to load prospects');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const response = await apiService.getProspectStats();
      setStats(response.data);
    } catch (error) {
      console.error('Failed to load stats:', error);
    }
  };

  const handleUpdateProspect = async (id, updates) => {
    try {
      await apiService.updateProspect(id, updates);
      setProspects(prospects.map(p => 
        p.id === id ? { ...p, ...updates } : p
      ));
      setEditingProspect(null);
      toast.success('Prospect updated successfully');
      loadStats(); // Refresh stats
    } catch (error) {
      console.error('Failed to update prospect:', error);
      toast.error('Failed to update prospect');
    }
  };

  const handleDeleteProspect = async (id) => {
    if (!window.confirm('Are you sure you want to delete this prospect?')) {
      return;
    }

    try {
      await apiService.deleteProspect(id);
      setProspects(prospects.filter(p => p.id !== id));
      toast.success('Prospect deleted successfully');
      loadStats(); // Refresh stats
    } catch (error) {
      console.error('Failed to delete prospect:', error);
      toast.error('Failed to delete prospect');
    }
  };

  const getScoreColor = (score) => {
    if (score >= 8) return 'score-high';
    if (score >= 6) return 'score-medium';
    return 'score-low';
  };

  const getPersonaColor = (persona) => {
    switch (persona) {
      case 'decision_maker': return 'persona-decision-maker';
      case 'sponsor': return 'persona-sponsor';
      case 'influencer': return 'persona-influencer';
      case 'champion': return 'persona-champion';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const filteredProspects = prospects.filter(prospect => {
    const matchesSearch = 
      prospect.profile_data?.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      prospect.profile_data?.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      prospect.company_domain?.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesSearch;
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Prospects</h1>
          <p className="text-gray-600 mt-1">
            Manage your prospect pipeline and track engagement
          </p>
        </div>
      </div>

      {/* Stats */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="card p-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-gray-900">{stats.total?.total_prospects || 0}</p>
              <p className="text-sm text-gray-500">Total Prospects</p>
            </div>
          </div>
          <div className="card p-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-green-600">{stats.total?.ideal_contacts || 0}</p>
              <p className="text-sm text-gray-500">Ideal Contacts</p>
            </div>
          </div>
          <div className="card p-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-blue-600">{stats.conversion_rates?.ideal_rate || 0}%</p>
              <p className="text-sm text-gray-500">Ideal Rate</p>
            </div>
          </div>
          <div className="card p-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-purple-600">{stats.total?.avg_score?.toFixed(1) || 0}</p>
              <p className="text-sm text-gray-500">Avg Score</p>
            </div>
          </div>
        </div>
      )}

      {/* Filters */}
      <div className="card p-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-4">
          <div className="lg:col-span-2">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search prospects..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input pl-10 w-full"
              />
            </div>
          </div>

          <select
            value={filters.status}
            onChange={(e) => setFilters({...filters, status: e.target.value})}
            className="input"
          >
            <option value="all">All Status</option>
            <option value="new">New</option>
            <option value="contacted">Contacted</option>
            <option value="responded">Responded</option>
            <option value="qualified">Qualified</option>
          </select>

          <select
            value={filters.persona_match}
            onChange={(e) => setFilters({...filters, persona_match: e.target.value})}
            className="input"
          >
            <option value="all">All Personas</option>
            <option value="decision_maker">Decision Maker</option>
            <option value="sponsor">Sponsor</option>
            <option value="influencer">Influencer</option>
            <option value="champion">Champion</option>
          </select>

          <select
            value={filters.is_ideal_contact}
            onChange={(e) => setFilters({...filters, is_ideal_contact: e.target.value})}
            className="input"
          >
            <option value="all">All Contacts</option>
            <option value="true">Ideal Contacts</option>
            <option value="false">Not Ideal</option>
          </select>

          <input
            type="number"
            placeholder="Min Score"
            min="1"
            max="10"
            value={filters.score_min}
            onChange={(e) => setFilters({...filters, score_min: e.target.value})}
            className="input"
          />
        </div>
      </div>

      {/* Prospects List */}
      {filteredProspects.length > 0 ? (
        <div className="card">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Prospect
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Company
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Score
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Persona
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredProspects.map((prospect) => (
                  <tr key={prospect.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="w-8 h-8 bg-gray-300 rounded-full mr-3 flex items-center justify-center">
                          <Users className="h-4 w-4 text-gray-600" />
                        </div>
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {prospect.profile_data?.name || 'Unknown'}
                          </div>
                          <div className="text-sm text-gray-500">
                            {prospect.profile_data?.title || 'No title'}
                          </div>
                        </div>
                      </div>
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{prospect.company_domain}</div>
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getScoreColor(prospect.score)}`}>
                          {prospect.score}/10
                        </span>
                        {prospect.is_ideal_contact && (
                          <Star className="h-4 w-4 text-yellow-500 ml-2" fill="currentColor" />
                        )}
                      </div>
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap">
                      {prospect.persona_match && (
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPersonaColor(prospect.persona_match)}`}>
                          {prospect.persona_match.replace('_', ' ')}
                        </span>
                      )}
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap">
                      {editingProspect === prospect.id ? (
                        <select
                          value={prospect.status}
                          onChange={(e) => handleUpdateProspect(prospect.id, { status: e.target.value })}
                          className="input text-xs"
                        >
                          <option value="new">New</option>
                          <option value="contacted">Contacted</option>
                          <option value="responded">Responded</option>
                          <option value="qualified">Qualified</option>
                        </select>
                      ) : (
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          prospect.status === 'new' ? 'bg-gray-100 text-gray-800' :
                          prospect.status === 'contacted' ? 'bg-blue-100 text-blue-800' :
                          prospect.status === 'responded' ? 'bg-green-100 text-green-800' :
                          'bg-purple-100 text-purple-800'
                        }`}>
                          {prospect.status}
                        </span>
                      )}
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                      <button
                        onClick={() => setEditingProspect(editingProspect === prospect.id ? null : prospect.id)}
                        className="text-blue-600 hover:text-blue-900"
                      >
                        <Edit3 className="h-4 w-4" />
                      </button>
                      <a
                        href={prospect.linkedin_url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-gray-400 hover:text-gray-600"
                      >
                        <ExternalLink className="h-4 w-4" />
                      </a>
                      <button
                        onClick={() => handleDeleteProspect(prospect.id)}
                        className="text-gray-400 hover:text-red-600"
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="card p-12">
          <div className="text-center">
            <Users className="h-16 w-16 mx-auto mb-4 text-gray-300" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {searchTerm || Object.values(filters).some(f => f !== 'all' && f !== '') 
                ? 'No prospects found' 
                : 'No prospects yet'
              }
            </h3>
            <p className="text-gray-600 mb-6">
              {searchTerm || Object.values(filters).some(f => f !== 'all' && f !== '')
                ? 'Try adjusting your search or filters'
                : 'Install the Chrome extension and visit LinkedIn profiles to start analyzing prospects'
              }
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Prospects;
