import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Building2, 
  Users, 
  ExternalLink, 
  TrendingUp, 
  Target,
  ArrowLeft,
  Brain,
  Star
} from 'lucide-react';
import { apiService } from '../services/api';
import toast from 'react-hot-toast';

const CompanyDetail = () => {
  const { domain } = useParams();
  const [company, setCompany] = useState(null);
  const [prospects, setProspects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [analyzing, setAnalyzing] = useState(false);
  const [analyzingPersonas, setAnalyzingPersonas] = useState(false);

  useEffect(() => {
    loadCompanyData();
    loadProspects();
  }, [domain]);

  const loadCompanyData = async () => {
    try {
      const response = await apiService.getCompany(domain);
      setCompany(response.data.company);
    } catch (error) {
      console.error('Failed to load company:', error);
      toast.error('Failed to load company data');
    } finally {
      setLoading(false);
    }
  };

  const loadProspects = async () => {
    try {
      const response = await apiService.getCompanyProspects(domain);
      setProspects(response.data.prospects);
    } catch (error) {
      console.error('Failed to load prospects:', error);
    }
  };

  const handleAnalyzeCompany = async () => {
    setAnalyzing(true);
    try {
      const response = await apiService.analyzeCompanyInfo(domain);
      setCompany(prev => ({
        ...prev,
        analysis_data: response.data.company.analysis_data
      }));
      toast.success('Company analysis completed');
    } catch (error) {
      console.error('Analysis failed:', error);
      toast.error('Failed to analyze company');
    } finally {
      setAnalyzing(false);
    }
  };

  const handleAnalyzePersonas = async () => {
    setAnalyzingPersonas(true);
    try {
      const response = await apiService.analyzeCompanyPersonas(domain, company.analysis_data);
      setCompany(prev => ({
        ...prev,
        personas_data: response.data.personas
      }));
      toast.success('Persona analysis completed');
    } catch (error) {
      console.error('Persona analysis failed:', error);
      toast.error('Failed to analyze personas');
    } finally {
      setAnalyzingPersonas(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="spinner"></div>
      </div>
    );
  }

  if (!company) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Company Not Found</h2>
        <Link to="/companies" className="btn-primary">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Companies
        </Link>
      </div>
    );
  }

  const getPersonaColor = (persona) => {
    switch (persona) {
      case 'decision_maker': return 'persona-decision-maker';
      case 'sponsor': return 'persona-sponsor';
      case 'influencer': return 'persona-influencer';
      case 'champion': return 'persona-champion';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const idealProspects = prospects.filter(p => p.is_ideal_contact);
  const avgScore = prospects.length > 0 ? prospects.reduce((sum, p) => sum + p.score, 0) / prospects.length : 0;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center">
          <Link 
            to="/companies" 
            className="mr-4 p-2 rounded-lg hover:bg-gray-100"
          >
            <ArrowLeft className="h-5 w-5 text-gray-600" />
          </Link>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">
              {company.analysis_data?.company_name || company.domain}
            </h1>
            <p className="text-gray-600">{company.domain}</p>
          </div>
        </div>

        <div className="flex space-x-3">
          {company.linkedin_url && (
            <a
              href={company.linkedin_url}
              target="_blank"
              rel="noopener noreferrer"
              className="btn-outline"
            >
              <ExternalLink className="h-4 w-4 mr-2" />
              View on LinkedIn
            </a>
          )}
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="card p-6">
          <div className="flex items-center">
            <div className="bg-blue-100 p-3 rounded-lg">
              <Users className="h-6 w-6 text-blue-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Prospects</p>
              <p className="text-2xl font-bold text-gray-900">{prospects.length}</p>
            </div>
          </div>
        </div>

        <div className="card p-6">
          <div className="flex items-center">
            <div className="bg-green-100 p-3 rounded-lg">
              <Star className="h-6 w-6 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Ideal Contacts</p>
              <p className="text-2xl font-bold text-gray-900">{idealProspects.length}</p>
            </div>
          </div>
        </div>

        <div className="card p-6">
          <div className="flex items-center">
            <div className="bg-purple-100 p-3 rounded-lg">
              <TrendingUp className="h-6 w-6 text-purple-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Avg Score</p>
              <p className="text-2xl font-bold text-gray-900">{avgScore.toFixed(1)}</p>
            </div>
          </div>
        </div>

        <div className="card p-6">
          <div className="flex items-center">
            <div className="bg-yellow-100 p-3 rounded-lg">
              <Target className="h-6 w-6 text-yellow-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Conversion Rate</p>
              <p className="text-2xl font-bold text-gray-900">
                {prospects.length > 0 ? Math.round((idealProspects.length / prospects.length) * 100) : 0}%
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Company Information */}
        <div className="card p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">Company Information</h3>
            {!company.analysis_data || Object.keys(company.analysis_data).length === 0 ? (
              <button
                onClick={handleAnalyzeCompany}
                disabled={analyzing}
                className="btn-primary text-sm"
              >
                <Brain className="h-4 w-4 mr-2" />
                {analyzing ? 'Analyzing...' : 'Analyze Company'}
              </button>
            ) : (
              <span className="px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                Analyzed
              </span>
            )}
          </div>

          {company.analysis_data && Object.keys(company.analysis_data).length > 0 ? (
            <div className="space-y-4">
              {company.analysis_data.description && (
                <div>
                  <h4 className="font-medium text-gray-900 mb-1">Description</h4>
                  <p className="text-gray-600 text-sm">{company.analysis_data.description}</p>
                </div>
              )}

              <div className="grid grid-cols-2 gap-4 text-sm">
                {company.analysis_data.industry && (
                  <div>
                    <p className="text-gray-500">Industry</p>
                    <p className="font-medium text-gray-900">{company.analysis_data.industry}</p>
                  </div>
                )}
                {company.analysis_data.size && (
                  <div>
                    <p className="text-gray-500">Company Size</p>
                    <p className="font-medium text-gray-900">{company.analysis_data.size}</p>
                  </div>
                )}
                {company.analysis_data.location && (
                  <div>
                    <p className="text-gray-500">Location</p>
                    <p className="font-medium text-gray-900">{company.analysis_data.location}</p>
                  </div>
                )}
                {company.analysis_data.business_model && (
                  <div>
                    <p className="text-gray-500">Business Model</p>
                    <p className="font-medium text-gray-900">{company.analysis_data.business_model}</p>
                  </div>
                )}
              </div>

              {company.analysis_data.products_services?.length > 0 && (
                <div>
                  <h4 className="font-medium text-gray-900 mb-2">Products & Services</h4>
                  <div className="flex flex-wrap gap-2">
                    {company.analysis_data.products_services.slice(0, 5).map((item, index) => (
                      <span key={index} className="px-2 py-1 bg-gray-100 rounded-md text-xs text-gray-700">
                        {item}
                      </span>
                    ))}
                  </div>
                </div>
              )}
            </div>
          ) : (
            <div className="text-center py-8">
              <Building2 className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <p className="text-gray-500">Company analysis not available</p>
              <p className="text-sm text-gray-400">Click "Analyze Company" to get detailed insights</p>
            </div>
          )}
        </div>

        {/* Target Personas */}
        <div className="card p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">Target Personas</h3>
            {!company.personas_data || Object.keys(company.personas_data).length === 0 ? (
              <button
                onClick={handleAnalyzePersonas}
                disabled={analyzingPersonas || !company.analysis_data}
                className="btn-primary text-sm"
              >
                <Brain className="h-4 w-4 mr-2" />
                {analyzingPersonas ? 'Analyzing...' : 'Analyze Personas'}
              </button>
            ) : (
              <span className="px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                Analyzed
              </span>
            )}
          </div>

          {company.personas_data?.personas ? (
            <div className="space-y-4">
              {company.personas_data.personas.map((persona, index) => (
                <div key={index} className="border rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <h4 className={`px-2 py-1 rounded-full text-xs font-medium ${getPersonaColor(persona.type)}`}>
                      {persona.type.replace('_', ' ')}
                    </h4>
                    <span className={`text-xs px-2 py-1 rounded ${
                      persona.influence_level === 'high' ? 'bg-green-100 text-green-800' :
                      persona.influence_level === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {persona.influence_level} influence
                    </span>
                  </div>

                  <div className="space-y-2 text-sm">
                    <p><span className="font-medium">Title:</span> {persona.title}</p>
                    <p><span className="font-medium">Department:</span> {persona.department}</p>
                    <p><span className="font-medium">Seniority:</span> {persona.seniority_level}</p>

                    {persona.pain_points?.length > 0 && (
                      <div>
                        <span className="font-medium">Pain Points:</span>
                        <ul className="mt-1 ml-4 list-disc text-xs text-gray-600">
                          {persona.pain_points.slice(0, 3).map((point, i) => (
                            <li key={i}>{point}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                </div>
              ))}

              {company.personas_data.targeting_strategy && (
                <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                  <h4 className="font-medium text-gray-900 mb-2">Targeting Strategy</h4>
                  <p className="text-sm text-gray-600">{company.personas_data.targeting_strategy}</p>
                </div>
              )}
            </div>
          ) : (
            <div className="text-center py-8">
              <Target className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <p className="text-gray-500">Persona analysis not available</p>
              <p className="text-sm text-gray-400">
                {!company.analysis_data ? 'Analyze company first, then generate personas' : 'Click "Analyze Personas" to identify key contacts'}
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Prospects */}
      <div className="card p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">Prospects</h3>
          <Link
            to={`/prospects?company=${domain}`}
            className="btn-outline text-sm"
          >
            View All Prospects
          </Link>
        </div>

        {prospects.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">
                    Name
                  </th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">
                    Title
                  </th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">
                    Score
                  </th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">
                    Persona
                  </th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {prospects.slice(0, 10).map((prospect) => (
                  <tr key={prospect.id} className="hover:bg-gray-50">
                    <td className="px-4 py-3 text-sm font-medium text-gray-900">
                      {prospect.profile_data?.name || 'Unknown'}
                    </td>
                    <td className="px-4 py-3 text-sm text-gray-600">
                      {prospect.profile_data?.title || 'No title'}
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex items-center">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          prospect.score >= 8 ? 'score-high' :
                          prospect.score >= 6 ? 'score-medium' : 'score-low'
                        }`}>
                          {prospect.score}/10
                        </span>
                        {prospect.is_ideal_contact && (
                          <Star className="h-4 w-4 text-yellow-500 ml-2" fill="currentColor" />
                        )}
                      </div>
                    </td>
                    <td className="px-4 py-3">
                      {prospect.persona_match && (
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPersonaColor(prospect.persona_match)}`}>
                          {prospect.persona_match.replace('_', ' ')}
                        </span>
                      )}
                    </td>
                    <td className="px-4 py-3">
                      <a
                        href={prospect.linkedin_url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:text-blue-500"
                      >
                        <ExternalLink className="h-4 w-4" />
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-8">
            <Users className="h-12 w-12 mx-auto mb-4 text-gray-300" />
            <p className="text-gray-500 mb-2">No prospects found for this company</p>
            <p className="text-sm text-gray-400">
              Use the Chrome extension to analyze LinkedIn profiles from this company
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CompanyDetail;
